@RestController
public class HelloController {
    @Autowired
    HelloService helloService;

    @GetMapping(&quot;/hello&quot;)
    //@GetMapping(value = &quot;/hello&quot;,produces = &quot;text/html;charset=utf-8&quot;) 可以简单解决中文乱码
    public String hello() {
        return helloService.sayHello();
    }
}

