    public ModelAndView myexception(MaxUploadSizeExceededException e) throws IOException {
        ModelAndView mv = new ModelAndView(&quot;myerror&quot;);
        mv.addObject(&quot;error&quot;, &quot;上传文件大小超出限制&quot;);
        return mv;
    }
}


